<?php
include_once 'permission.php';
if ($_SERVER['REQUEST_METHOD'] == 'POST') {
    $vtabani = new mysqli("localhost", "root", "", "ozluk");

    $cs_id = $vtabani->real_escape_string($_POST['cs_id']);
    $day = $vtabani->real_escape_string($_POST['day']);
    $period = $vtabani->real_escape_string($_POST['period']);

    // Define the query based on listType, listName, and day
    $query = "SELECT timetable_id, course_id, program_id, lecturer_id, classroom_id, course_start, course_end FROM courseschedule WHERE cs_id='$cs_id' and course_day='$day'
    and '$period' between course_start and course_end";

    $sorgu = $vtabani->query($query);

    // Generate the HTML for the dayContent div
    $html = '';
    foreach ($sorgu as $veri) {

        // Retrieve data from database
        $courseQuery = "SELECT course_name, course_mandatory FROM course WHERE course_id=" . $veri['course_id'];
        $courseSorgu = $vtabani->query($courseQuery);
        $courseResult = $courseSorgu->fetch_assoc();
        $course_name = $courseResult['course_name'];
        $course_mandatory = $courseResult['course_mandatory'];

        if ($course_mandatory == 1) {
            $mandatory = "Z";
        } else {
            $mandatory = "S";
        }

        $lecturerQuery = "SELECT lecturer_name, lecturer_mail FROM lecturer WHERE lecturer_id=" . $veri['lecturer_id'];
        $lecturerSorgu = $vtabani->query($lecturerQuery);
        $lecturerResult = $lecturerSorgu->fetch_assoc();
        $lecturer_name = $lecturerResult['lecturer_name'];
        $lecturer_mail = $lecturerResult['lecturer_mail'];

        $classroomQuery = "SELECT classroom_name FROM classroom WHERE classroom_id=" . $veri['classroom_id'];
        $classroomSorgu = $vtabani->query($classroomQuery);
        $classroomResult = $classroomSorgu->fetch_assoc();
        $classroom_name = $classroomResult['classroom_name'];

        $programQuery = "SELECT program_name FROM program WHERE program_id=" . $veri['program_id'];
        $programSorgu = $vtabani->query($programQuery);
        $programResult = $programSorgu->fetch_assoc();
        $program_name = $programResult['program_name'];

        $startQuery = "SELECT period_start FROM period WHERE period_no=" . $veri['course_start'];
        $startSorgu = $vtabani->query($startQuery);
        $startResult = $startSorgu->fetch_assoc();
        $period_start = new DateTime($startResult['period_start']);

        $endQuery = "SELECT period_end FROM period WHERE period_no=" . $veri['course_end'];
        $endSorgu = $vtabani->query($endQuery);
        $endResult = $endSorgu->fetch_assoc();
        $period_end = new DateTime($endResult['period_end']);

        $course_start = $veri['course_start'];
        $course_end = $veri['course_end'];

    
        // Generate the HTML for the card
        $html .= '
        <div class="card" id="' . $cs_id . '" data-day="' . $day . '" data-cs-id="' . $cs_id . '">
            <div class="card__contentArea">
                <h3 class="card__title" id="baslik">('.$mandatory.')' . $course_name . '</h3>
                <div class="card__contentGroup">
                    <a class="card__content" id="icerik-4">' . $program_name . '</a>
                    <p class="card__content" id="icerik-1">' . $classroom_name . '</p>
                    <p class="card__content" id="icerik-2">' . $lecturer_name . '</p>
                </div> 
                <div class="card__date" data-start="' . $course_start . '" data-end="' . $course_end . '" id="icerik-4">' . $period_start->format('H:i') . ' - ' . $period_end->format('H:i') . '</div>
            </div> 
